import axios from "axios";

let starUsers = {};
let totalLength = 0;
function getStarUsers() {
  // Simple GET request using axios
  axios.get('https://swapi.dev/api/people/')
    .then(response => {
      var starData = (response.data.results)
      console.log(starData)
      totalLength = starData.length
      for (let user of starData) {

       let species = user["species"]

        if (species.length == 0) {
          species = "Human"
          if (starUsers[species]) {
            starUsers[species].push(user)
          }
          else {
            starUsers[species] = [user]
          }
        }
        else {

          axios.get(species)
            .then(spResponse => {

              species = spResponse.data["name"]

            }).finally(() => {

              if (starUsers[species]) {
                starUsers[species].push(user)
              } else {
                starUsers[species] = [user]
              }


            }
            )
        }
      }
      function waitForElement() {
        let pendLength = 0;
        for (var sublist of starUsers) {
          pendLength += sublist.length
        }

        if (pendLength == totalLength) {
          console.log(starUsers)
          for(let list of starUsers){
            var speciesCount=starUsers[list]
            console.log(speciesCount)
            for(let user of speciesCount){
              console.log(user)
            }
          }
          // ReactDOM.render(starUsers["Droid"].map(starUser => {
          //   return <div className="card" key={starUser['name']}>{starUser['name']}</div>
          // }), document.getElementById('root'));

        }
        else {
          setTimeout(waitForElement, 200);
        }
      }
      waitForElement()
    }
    )
}

