import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { render } from "react-dom";
import axios from "axios";

var starUsers={};

function getStarUsers() {
  // Simple GET request using axios
  axios.get('https://swapi.dev/api/people/')
    .then(response => {
      var starData=(response.data.results)
      for(var user of starData){
        var species=user["species"]
        if (species.length<1){
          species="Human"
          if(starUsers[species]){
            starUsers[species].push(user)
          }
          else{
            starUsers[species]=[user]
          }
        }
        else{
          axios.get(species)
          .then(spResponse => {
            species=spResponse.data["name"]
          }).finally(() => {
            if(starUsers[species]){
              starUsers[species].push(user)
            }else{
              starUsers[species]=[user]
            }}
          )
        }
      }
    }
    ).finally(() => {
      console.log(starUsers)

      ReactDOM.render(starUsers["Human"].map(starUser => {
        return <div class="card">{starUser['name']}</div>
      }), document.getElementById('root'));
    });;
}
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
getStarUsers()



