import ReactDOM from 'react-dom'
import React from 'react'
import { render } from "react-dom";
import axios from "axios";

function getUserData() {
    console.log('https://swapi.dev/api/people/' + '?search=' + window.location.href.split('/')[4])
    axios.get('https://swapi.dev/api/people/' + '?search=' + window.location.href.split('/')[4])
        .then(response => {
            var userList = response.data.results[0]
            userList["newHome"]="no"
            axios.get(userList["homeworld"])
                .then(spResponse => {
                    userList["newHome"] = (spResponse.data["name"])
                })
            for (let ships of userList["starships"]) {
                axios.get(ships)
                    .then(spResponse => {
                        if (userList["newShips"]) {
                            userList["newShips"].push(spResponse.data["name"])
                        }
                        else {
                            userList["newShips"] = [spResponse.data["name"]]
                        }

                    })
            }
            for (let ships of userList["vehicles"]) {
                axios.get(ships)
                    .then(spResponse => {
                        if (userList["newVehicles"]) {
                            userList["newVehicles"].push(spResponse.data["name"])
                        }
                        else {
                            userList["newVehicles"] = [spResponse.data["name"]]
                        }
                    })
            }

            var finalCard;
            if (userList["species"] == 0) {
                console.log(userList)

            }
            else {
                // axios.get(species)
                //     .then(spResponse => {
                //         species = spResponse.data["name"]
                //         if (speciesList.indexOf(species) == -1) {
                //             speciesList.push(species)
                //         }
                //         user["species"] = species
                //         starUsers.push(user)
                //     })
            }
            function waitForElement() {

                if ( userList["newHome"]!="no") {
                    if (userList["species"] == 0) {
                        console.log(userList["newHome"])
                        userList["species"] = "Human"
                        finalCard=<h1 className="title Human soleUser">{userList["name"]}
                        <div className="soleUser"> <p className="category">Born:&nbsp;&nbsp; </p>{userList["birth_year"]}  </div>
                        <div className="soleUser"> <p className="category">Eye Color:&nbsp;&nbsp; </p>{userList["eye_color"]}  </div>
                        <div className="soleUser"> <p className="category">Hair Color:&nbsp;&nbsp; </p>{userList["hair_color"]}  </div>
                        <div className="soleUser"> <p className="category">Height:&nbsp;&nbsp; </p>{userList["height"]}  </div>
                        <div className="soleUser"> <p className="category">Mass: &nbsp;&nbsp; </p>{userList["mass"]}  </div>
                        <div className="soleUser"> <p className="category">Home: &nbsp;&nbsp;</p>{userList["newShips"]}  </div>
                        <div className="soleUser"> <p className="category">Skin: &nbsp;&nbsp;</p>{userList["skin_color"]}  </div>
                        <div className="soleUser"> <p className="category">Species:&nbsp;&nbsp; </p>{userList["species"]}  </div>

                        </h1>
                    }
                    ReactDOM.render(
                        finalCard, document.getElementById('root'));



                }
                else {
                  setTimeout(waitForElement, 200);
                }
              }
              waitForElement()
            })

}

export default function User() {
    getUserData()
    return <p>Hello, world!</p>
}
