import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { render } from "react-dom";
import axios from "axios";

var starUsers={};
var totalLength=0;
function getStarUsers() {
  // Simple GET request using axios
  axios.get('https://swapi.dev/api/people/')
    .then(response => {
      var starData=(response.data.results)

      totalLength=starData.length
      for (let user of starData){

        var species=user["species"]

        if(species.length==0){
          species="Human"
          if(starUsers[species]){
            starUsers[species].push(user)
          }
          else{
            starUsers[species]=[user]
          }
        }
        else{
          let otherUser=user
          axios.get(species)
          .then(spResponse => {
            console.log(otherUser)
            species=spResponse.data["name"]

          }).finally(() => {

            if(starUsers[species]){
              starUsers[species].push(user)
            }else{
              starUsers[species]=[user]
            }


          }
          )
        }
      }
      function waitForElement(){
        var pendLength=0
        for(var sublist in starUsers){
          pendLength+=sublist.length
        }

        if(pendLength==totalLength){
            console.log(starUsers)
            console.log("YESSSSS")
        }
        else{
            setTimeout(waitForElement, 200);
        }
    }
    waitForElement()

    }
