import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { render } from "react-dom";
import axios from "axios";

let starUsers = [];
let totalLength = 0;
let speciesList=[]
function getStarUsers() {
  // Simple GET request using axios
  axios.get('https://swapi.dev/api/people/')
    .then(response => {
      var starData = (response.data.results)

      totalLength = starData.length
      for (let user of starData) {

       let species = user["species"]

        if (species.length == 0) {
          species = "Human"
          if(speciesList.indexOf(species)==-1){
            speciesList.push(species)
          }
          user["species"]=species
          starUsers.push(user)
        }
        else {

          axios.get(species)
            .then(spResponse => {

              species = spResponse.data["name"]
              if(speciesList.indexOf(species)==-1){
                speciesList.push(species)
              }
              user["species"]=species
              starUsers.push(user)
            })
        }
      }
      function waitForElement() {

        if (starUsers.length==totalLength) {

          let sortedList=[]
          var anotherSpeciesList=speciesList.slice();

          while(speciesList.length>0){
            var currentSpecies=speciesList[speciesList.length-1]
            for(let user of starUsers){

              if(user["species"]==currentSpecies){
                sortedList.push(user)
              }
            }
            speciesList.pop(currentSpecies)
          }
          var lastSpecies=""

          ReactDOM.render(starUsers.map(starUser => {
            if(lastSpecies!=starUser["species"]){
              lastSpecies=starUser["species"]
              console.log(anotherSpeciesList)
              anotherSpeciesList.pop(lastSpecies)
              if(!anotherSpeciesList.length==0){
                return <div className="card" key={starUser['name']}>{starUser['name']}</div>
              }
              else{
                return <h1>sam</h1>
              }

            }
            else{
              return <div className="card" key={starUser['name']}>{starUser['name']}</div>
            }

          }), document.getElementById('root'));

        }
        else {
          setTimeout(waitForElement, 200);
        }
      }
      waitForElement()
    }
    )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

